var fs = require('fs');

var Parser = function(file) {
    if ("object" === typeof file)
        this.file = file;
    else if ("string" === typeof file) {
        fs.readFile(file, function (err, data) {
            if (err) throw err;
            console.log(data);
        });
    }
    this.file = file;
    console.log("Voila le file : " + this.file);
}

