var csvParser = require('./csv/parser.js');
var xmlParser = require('./xml/parser.js');
var jsonParser = require('./json/parser.js');
var xlsParser = require('./xls/parser.js');

var GenericParser = function(inputType) {
  var types = {
      "csv" : csvParser,
      "xml" : xmlParser,
      "json": jsonParser,
      "xls" : xlsParser
  };
  //TODO: GESTION D'ERREUR SUR INPUTTYPE
  return new types[inputType]();
};

module.exports = GenericParser;