var fs = require('fs');
var xml2js = require('xml2js');

function parserXML() {
    this.parse = parseXML;
    return this;
}

function parseXML(fileName, noRam, callback) {
    var parser = new xml2js.Parser();
    console.log("VOILA LE NOM DU FILE : " + fileName);
    fs.readFile(fileName, function(err, data) {
    parser.parseString(data, function (err, result) {
            callback(result);
        });
    });
}

module.exports = parserXML;